mapper.insert:

	select
	nvl(max(seq),0) + 1 from items

INSERT INTO items
		(seq
		,title
		,writer
		,content
		,regdate

	,upload_file_name

		)
		VALUES
		(#{seq}
		,#{title}
		,#{writer}
		,#{content}
		,SYSDATE

	,#{uploadFileName}

		)


{call items_ins_proc(#{title}
					,#{writer}
					,#{content}
					,#{seq, mode=OUT, jdbcType=NUMERIC, javaType=java.lang.Integer}
					,#{outMsg, mode=OUT, jdbcType=VARCHAR, javaType=String}
)}

mapper.update:

UPDATE items
SET    title   = #{title}
      ,content = #{content}
      ,writer  = #{writer}
WHERE  seq = #{seq}
