mapper.update:
UPDATE SALES 
SET SALE_CODE = 'S' || TO_CHAR(SYSDATE, 'YYYYMMDD') || (
													SELECT LPAD(NVL(COUNT(*), 0) + 1,3,'0') 
													FROM sales 
													WHERE sale_date = SYSDATE
												)
	, SALE_DATE = SYSDATE 
	, DELIVERY_EMP = #{deliveryEmp} 
	, WAREHOUSE = #{warehouse} 
	, ORDER_PERMIT = 'Y' 
	WHERE SALE_CODE = #{saleCode}

UPDATE SALES 
SET ORDER_PERMIT = 'F' 
WHERE SALE_CODE = #{saleCode}
