mapper.select:
SELECT * FROM stock_in_out_vo
AND item_code = #{itemCode}
ORDER BY txn_date


SELECT v.*, common.get_vendor_name(v.vendor_code) vendor_name
FROM   stock_onhands_vo v


	
		AND item_code like '%'||#{itemCode}||'%'
	
	
		AND item_name like '%'||#{itemName}||'%'
	
	
		AND vendor_code like #{vendorCode}||'%'
	
	
		AND SUBSTR(item_code,1,1) = #{group1}
	
	
		AND SUBSTR(item_code,2,1) = #{group2}
	
	
		AND SUBSTR(item_code,3,1) = #{group3}
	

ORDER BY item_code


SELECT common.get_vendor_name(v.vendor_code) vendor_name, v.*
FROM   purchase_request_vo v


	
		AND SUBSTR(item_code,1,1) = #{group1}
	
	
		AND SUBSTR(item_code,2,1) = #{group2}
	
	
		AND SUBSTR(item_code,3,1) = #{group3}
	
	
		AND vendor_code = #{vendorCode}
	

ORDER BY vendor_code, item_code

SELECT p.*, common.get_item_name(p.purchase_item) item_name FROM purchase_details p
AND purchase_code = #{purchaseCode}
ORDER BY purchase_detail_code

SELECT lk.lookup lookups ,lk.lookup_description ,lv.lookup ,lv.lookup_code ,lv.lookup_values ,lv.descriptions ,lv.flag ,lv.dff ,lv.reflag FROM lookups lk JOIN lookup_values lv ON lk.lookup = lv.lookup
AND lk.lookup = #{_parameter}
ORDER BY lk.lookup ,lv.lookup_code

mapper.update:
		
		UPDATE lookup_values
		SET    lookup_values = #{lookupValues}
		      ,descriptions  = #{descriptions}
		      ,flag          = #{flag}
		      ,dff           = #{dff}
		      ,reflag        = #{reflag}
		WHERE  lookup_idx = #{lookupIdx}

mapper.select:
SELECT lv.descriptions "grp_name" ,lv.lookup_code "grp_code" FROM lookups lk ,lookup_values lv WHERE lk.lookup = lv.lookup AND lk.lookup = 'ITEM_GROUP2'
AND lv.dff = #{_parameter}
ORDER BY lv.descriptions

SELECT lv.descriptions "grp_name" ,lv.lookup_code "grp_code" FROM lookups lk ,lookup_values lv WHERE lk.lookup = lv.lookup AND lk.lookup = 'ITEM_GROUP3'
AND lv.dff = #{_parameter}
ORDER BY lv.descriptions
